import React, { Component } from 'react';
import AvatarChange from '../AvatarChange/index.js';
import InputText from '../InputText/index.js';
import CustomSelect from '../CustomSelect/CustomSelect.js';
import ColorSelect from '../CustomSelect/ColorSelect.js';
import './index.css';

class Form extends Component {
	constructor(props) {
		super(props);

		this.state = {

		}
	}

	getState = (stateName=null, value=null) => {this.setState({stateName: value}); console.log(this.state.stateName, stateName)}

	render() {
		return (
			<div className="form-container">

				<div className="avatarchange-container">
					<AvatarChange />
				</div>

				<div className="inputs-container">
					<InputText label="ФИО" name="fullname" placeholder="Иванов Иван Иванович" getState={this.getState}/>
					<InputText label="Email" name ="email" placeholder="ivanov@gmail.com" getState={this.getState}/>
					<CustomSelect label="Специальность" name="specilty" options={[]} selected="Выбрать" getState={this.getState}/>
					<CustomSelect label="Группа" name="group" options={[]} selected="Выбрать" getState={this.getState}/>
					<InputText label="Рейтинг" name="rating" placeholder="0" getState={this.getState}/>
					<CustomSelect label="Пол" name="sex" selected="Выбрать" options={[]} getState={this.getState}/>
					<ColorSelect label="Любимый цвет" name="color" selected="Выбрать" options={[]} type="colorSelect" getState={this.getState}/>					
				</div>

          </div>
		);
	}



}

export default Form
