import React, {Component} from 'react';
import './index.css';

class InputSelect extends Component {
	constructor(props) {
		super(props);

		this.state = {
			label: this.props.label,
			options: this.props.options,
			selected: this.props.selected,
			isOpen: false,
		}
	}

	render() {
		return (
			<div className="input-container">
				<h4>{this.state.label}</h4>
				<button className="custom-select" onClick={this.switchVisibility}>{this.state.selected}</button>
				<div className="options-container" style={{display: this.state.isOpen ? "grid" : "none"}}>
					{this.getOptions()}
				</div>
			</div>	
		);
	}

	switchVisibility = () => this.setState({isOpen: !this.state.isOpen});

	getOptions() {
		let optionsArray = [];
		for (let id in this.state.options) {
			let temp = [];
			temp = [
				<input id={id} className="option-input" type="radio" value={this.state.options.id}></input>,
				<label for={id} className="option-label" onClick={() => this.selectOption(this.state.options[id]), id} >{this.state.options[id]}</label>
			];

			optionsArray = optionsArray.concat(temp);
		}
		return optionsArray;
	}

	selectOption = (option, id) => this.setState({selected: option,
											  selectedID: id}) 

}

export default InputSelect
