import React, {Component} from 'react';
import Header from './Header/index.js';
import ButtonAddStudent from './ButtonAddStudent/index.js'
import TextStudents from './TextStudents/index.js';
import SearchLine from './SearchLine/index.js';
import SortButton from './SortButton/index.js';
import TableStudents from './TableStudents/index.js';
import ButtonToMainPage from './ButtonToMainPage/index.js';
import './fonts/Geometria/stylesheet.css';
import './App.css';


class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      'isMainPage': false,
    }
  }

  render(props) {
    return (
      <div>
        <Header />

        {this.state.isMainPage ? this.renderMainPage() : this.renderAddStudentPage()}

      </div>
    );
  }

  renderMainPage() {
    return(
      <div className="container">
        <div className="container-text-and-button">
          <TextStudents />
          <ButtonAddStudent />
        </div>
        <div className="container-tools">
          <SearchLine />
          <SortButton />
        </div>
        <TableStudents />
      </div>
    );
  }

  renderAddStudentPage() {
    return (
      <ButtonToMainPage />
    );
  }

}

export default App;
