import React, { Component } from 'react';
import AvatarChange from '../AvatarChange/index.js';
import InputText from '../InputText/index.js';
import CustomSelect from '../CustomSelect/CustomSelect.js';
import ColorSelect from '../CustomSelect/ColorSelect.js';
import Button from '../Button/index.js';
import './index.css';

class Form extends Component {
	constructor(props) {
		super(props);

		this.getState = this.getState.bind(this);
	}

	getState = (stateName=null, value=null) => {
		this.setState({[stateName]: value});
	}

	async sendPostRequest(data) {
		const response = await fetch("/", {
			method: 'POST', 
			headers: {
			  'Content-Type': 'application/json'
			  // 'Content-Type': 'application/x-www-form-urlencoded',
			},
			body: JSON.stringify(data) 
		  });	

		  return await response.json();
	}

	render() {
		return (
			<div>

				<div className="avatarchange-container">
					<AvatarChange />
				</div>

				<div className="inputs-container">
					<InputText label="ФИО" name="fullname" placeholder="Иванов Иван Иванович" sendState={this.getState}/>
					<InputText label="Email" name ="email" placeholder="ivanov@gmail.com" sendState={this.getState}/>
					<CustomSelect label="Специальность" name="specilty" options={[1, 2]} value="Выбрать" sendState={this.getState}/>
					<CustomSelect label="Группа" name="group" options={["da", "pizda"]} value="Выбрать" sendState={this.getState}/>
					<InputText label="Рейтинг" name="rating" placeholder="0" sendState={this.getState}/>
					<CustomSelect label="Пол" name="sex" value="Выбрать" options={[]} sendState={this.getState}/>
					<ColorSelect label="Любимый цвет" name="color" value="Выбрать" options={[]} type="colorSelect" sendState={this.getState}/>	
				</div>

				<Button label="Создать" onClick={() => this.sendPostRequest(this.state)} />

			</div>

		);
	}



}

export default Form
