import React, {Component} from 'react';
import './index.css';
import InputText from '../InputText';

class InputSelect extends Component {
	constructor(props) {
		super(props);

		this.state = {
			label: this.props.label,
			options: this.props.options,
			selected: thisp.props.selected,
		}
	}

	render() {
		return (
			<div className="input-container">
				<label>{this.state.label}</label>
				<button className="custom-select">{this.state.selected}</button>
				<div className="options-container" style="display: none">

				</div>
			</div>	
		);
	}

	getOptions() {
		return this.state.options.map((option) => <option value={option} label={option}></option>)
	}
}

export default InputSelect
