import React from 'react';
import './CustomSelect.css';
import CustomSelect from './CustomSelect.js';

class SortSelect extends CustomSelect {
	constructor(props) {
		super(props);

		this.state = {
			name: props.name,
			options: props.options || [],
			value: props.value,
			selectedID: props.selectedID || -1,
			isOpen: false,
		}

	}

	getIcon = () => <img className="sortSelect-icon" src="./images/sort.svg"></img>;

	render(props) {
		return (
			<div className="input-container">
				<button className="sort-select"  onClick={this.switchVisibility}
												 onBlur={() => setTimeout(this.setHidden, 200)} >

					<span >{this.state.value}</span>
					{this.getIcon()}

				</button>

				<div className="options-container" style={{display: this.state.isOpen ? "grid" : "none"}}>
					{this.getOptions()}
				</div>

			</div>	
		);
	}

}

export default SortSelect
